package com.hefshine.conditional;

public class Hw5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'g';

        if(ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u' )
        {
            System.out.println(ch + " is vowel");
        }
        else
        { 
            System.out.println(ch + " is consonant");
        }
	}

}
