package com.hefshine.example;

public class Complexjava {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=0;
		for(int j=1;j<=4;j=j+1)
		
		{
			for(int i=1;i<=4;i=i+1)
			{	
				count=count+1;
				System.out.print(count+" ");
				
			}
			
			System.out.println();
		}


	}

}
