package com.hefshine.example;

public class Complexjava5 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j=1;j<=3;j=j+1)
			
		{
			for(int i=5;i>=1;i=i-1)
			{	
				System.out.print(i+" ");
				
			}
			
			System.out.println();
		}

	}

}
